    private void cambiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cambiarActionPerformed

        int pos =cambiar.getSelectedIndex();
        
        Color bb;
        
        switch (pos){
            case 0:  
                bb=new Color(200,0,0); 
                fondo.setBackground(bb);
                break;
             case 1:  
                bb=new Color  (0,200,0);         
                 fondo.setBackground(bb);
                 break;
               
                 case 2:  
                 bb=new Color  (0,0,200);         
                 fondo.setBackground(bb);
                 break;
    }    
        
    }//GEN-LAST:event_cambiarActionPerformed

