    private void sliderStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_sliderStateChanged
        // TODO add your handling code here:
        int pos=cambiar.getSelectedIndex();
        int sl = slider.getValue();
        
        Color bb;
        
        switch (pos){
            case 0:  
                bb=new Color(sl,0,0); 
                fondo.setBackground(bb);
                break;
             case 1:  
                bb=new Color  (0,sl,0);         
                 fondo.setBackground(bb);
                 break;
               
                 case 2:  
                 bb=new Color  (0,0,sl);         
                 fondo.setBackground(bb);
                 break;
    }  
        
    }//GEN-LAST:event_sliderStateChanged

